mapper.select:
select i.*, c.category_name from influencer i join category c
using(category_id)
where influencer_num = #{influencerNum}

mapper.update:
update influencer
password = #{password}, name = #{name}, nickname = #{nickname}, mobile_number = #{mobileNumber}, address = #{address}, address_detail = #{addressDetail}
where influencer_num = #{influencerNum}
